package com.example.kuliner.fragments;

import android.os.Bundle;

import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.kuliner.Items_rv;
import com.example.kuliner.R;
import com.example.kuliner.RecyclerViewAdapter;

import java.util.ArrayList;
import java.util.List;

public class Makanan_favorite extends Fragment {

    private RecyclerView recyclerView;
    private RecyclerViewAdapter adapter;
    private List<Items_rv> items;

    public void MakananFavorite() {
        // Required empty public constructor
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_makanan_favorite, container, false);
        recyclerView = view.findViewById(R.id.rvMakananFavorite);
        recyclerView.setLayoutManager(new GridLayoutManager(getContext(), 1));

        items = new ArrayList<>();
        items.add(new Items_rv("Nasi Goreng", "Makanan Favorite", "https://www.andy-cooks.com/cdn/shop/articles/20230527000540-andy-20cooks-20-20nasi-20goreng.jpg?v=1690354810", "Nasi goreng, hidangan khas Asia yang lezat, terdiri dari nasi yang digoreng bersama bumbu-bumbu tradisional seperti bawang, bumbu dapur, dan tambahan beragam seperti telur, sayuran, dan daging."));
        items.add(new Items_rv("Mie Goreng", "Makanan Favorite", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSn9sQrjrtFCkJAGjLgVOrK9ekM3rt92HQX3A&usqp=CAU", "Indomie, mi instan yang populer dan praktis, menjadi pilihan favorit dengan rasa khasnya yang lezat dan variasi topping yang beragam, cocok dinikmati sebagai sajian cepat dan menyenangkan."));
        items.add(new Items_rv("Soto", "Makanan Favorite", "data:image/jpeg;base64,", "Soto, hidangan sup khas Indonesia yang lezat, terdiri dari kaldu ayam atau daging sapi yang gurih, disajikan dengan irisan daging, telur, tauge, dan rempah-rempah yang kaya cita rasa, sempurna untuk menghangatkan perut dan menyegarkan selera."));
        items.add(new Items_rv("Bakso", "Makanan Favorite", "https://awsimages.detik.net.id/community/media/visual/2020/01/03/c993eff2-ea2b-412a-b7ab-69ed1011a2b7_43.jpeg?w=1200", "Bakso, hidangan favorit Indonesia yang terkenal dengan bola dagingnya yang kenyal, disajikan dalam kuah kaldu yang kaya rasa, seringkali dilengkapi dengan mie, tauge, dan bumbu-bumbu khas, menjadi pilihan populer untuk menikmati cita rasa autentik Indonesia."));

        // Inisialisasi adapter dan set ke RecyclerView
        adapter = new RecyclerViewAdapter(getContext(), items);
        recyclerView.setAdapter(adapter);

        return view;
    }
}